mapper.select:
select
  id, acttype, alertkey, devtype, fixalarmlevel, issuedate, liabilitycity, location, networklevel, operationtype, returnresult, stickevent, suspectfaultreasonclass, suspectfaultreasonsection, tasktitle, tempstr3, tolllevel, worktip, wsid
from test.order_info


        and id = #{id}


        and acttype = #{acttype}


        and alertkey = #{alertkey}


        and devtype = #{devtype}


        and fixalarmlevel = #{fixalarmlevel}


        and issuedate = #{issuedate}


        and liabilitycity = #{liabilitycity}


        and location = #{location}


        and networklevel = #{networklevel}


        and operationtype = #{operationtype}


        and returnresult = #{returnresult}


        and stickevent = #{stickevent}


        and suspectfaultreasonclass = #{suspectfaultreasonclass}


        and suspectfaultreasonsection = #{suspectfaultreasonsection}


        and tasktitle = #{tasktitle}


        and tempstr3 = #{tempstr3}


        and tolllevel = #{tolllevel}


        and worktip = #{worktip}


        and wsid = #{wsid}
